var _ = require("underscore");
module.exports = {
	"_cache": {},
	"render": function(path, params) {
		if(!this._cache.hasOwnProperty(path)) {
			var fs = require('fs');
			try {
				var contents = fs.readFileSync(path).toString();
				this._cache[path] = _.template(contents);
			}
			catch(e) {
			}
		}
		if(this._cache.hasOwnProperty(path)) {
			if(params == undefined) params = {};
			return this._cache[path](params);
		}
		return false;
	}
}